#include <iostream>#include <string>using namespace std;int main() {    string line;    cout << "请输入字符串" << endl;    int letters = 0, spaces = 0, digits = 0, others = 0;    // 输入字符串    getline(cin, line);    // 遍历字符串中的每个字符    for (char c : line) {        if ((c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z')) {            letters++;        }        else if (c == ' ') {            spaces++;        }        else if (c >= '0' && c <= '9') {            digits++;        }        else {            others++;        }    }    // 输出结果    cout << "英文字母个数: " << letters << endl;    cout << "空格个数: " << spaces << endl;    cout << "数字字符个数: " << digits << endl;    cout << "其他字符个数: " << others << endl;    return 0;}